mapper.delete:
delete from comment
 where cnum = #{cnum}
   and post_num = #{post_num}
   and user_num = #{user_num}

mapper.select:
select ctext
  from comment
 where cnum     = #{cnum}
   and post_num = #{post_num}
   and user_num = #{user_num}

mapper.update:
update comment
   set ctext    = #{ctext}
 where cnum     = #{cnum}
   and post_num = #{post_num}
   and user_num = #{user_num}
