mapper.select:
select count(*)
  from likes
 where post_pnum = #{pnum}
   and user_id = #{user_num}

select count(*)
  from likes
 where post_pnum = #{pnum}

select count(*)
  from post
 where pnum = #{pnum}
   and user_num = #{user_num}

mapper.update:
update post
   set ptext = #{ptext}
     , pdatetime_modified = now()

     , pphoto = #{fileContent}

 where pnum = #{pnum}
   and user_num = #{user_num}

mapper.delete:
delete from post
 where pnum = #{pnum}
   and user_num = #{user_num}
