mapper.insert:
INSERT
  INTO recruit(rphoto
             , image_data
             , rtitle
             , rtext
             , rdatetime
             , rcount
             , rloc
             , radrs
             , etc
             , sigungu
             , user_num
              ) VALUES (
               #{rphotos}
             , #{imageData}
             , #{rtitle}
             , #{rtext}
             , str_to_date(concat(#{rdate}, #{rtime}), '%Y-%m-%d%H:%i')
             , #{rcount}
             , #{rloc}
             , #{radrs}
             , #{etc}
             , #{sigungu}
             , #{userNum}
              )

mapper.update:
UPDATE recruit
   SET rtitle     = #{rtitle}
     , rtext      = #{rtext}

     , rphoto     = #{rphotos}
     , image_data = #{imageData}

     , rdatetime  = str_to_date(concat(#{rdate}, #{rtime}), '%Y-%m-%d%H:%i')
     , rcount     = #{rcount}
     , rloc       = #{rloc}
     , radrs      = #{radrs}
     , etc        = #{etc}
     , sigungu    = #{sigungu}
     , user_num   = #{userNum}
 WHERE rnum       = #{rnum}
   AND user_num   = #{userNum}

mapper.delete:
DELETE FROM recruit
 WHERE rnum = #{rnum}
   AND user_num = #{userNum}

mapper.select:
	select count(*)
	  from rjoin
 	 where recruit_rnum = #{rnum}
	  and user_num      = #{userNum}

	select count(*)
	  from rjoin
 	 where recruit_rnum = #{rnum}

SELECT count(*)
  FROM scrap
 WHERE recruit_rnum = #{rnum}
   AND user_num = #{userNum}
